mapper.select:
SELECT `t_order_comment`.cid, `t_order_comment`.emid, `t_order_comment`.hkid, `t_order_comment`.score, `t_order_comment`.content, `t_order_comment`.create_time, `t_base_housekeeper`.headimageurl, `t_base_housekeeper`.username, `t_base_housekeeper`.name FROM t_base_housekeeper , t_order_comment WHERE t_order_comment.hkid=t_base_housekeeper.hkid and t_order_comment.hkid=#{hkid} and t_order_comment.state = 1
and t_order_comment.emid = #{emid}
ORDER BY create_time desc

SELECT * FROM `t_base_housekeeper`
and username like CONCAT('%',#{username},'%')
and name =like CONCAT('%',#{name},'%')
and state = #{state}
and servicestate = #{servicestate}
ORDER BY create_time desc

mapper.update:
  		update t_base_housekeeper_certificate

	    state="0",update_time = now()

  		where hkid=#{hkid}
  		and cid=#{cid}
